using UnityEngine;using System.Collections;public class DestroySprite : MonoBehaviour {    private float defSpeed = 2.0f;    public float destroySpeed = 0;    public GameObject parentObject;    private DropObjectStats drObjStats;    private DestroySpriteSound drSound;    void Start(){        destroySpeed = defSpeed;        if(parentObject != null)drSound = GetComponent<DestroySpriteSound>();    }    void OnCollisionEnter2D(Collision2D col) {        if(col.gameObject.CompareTag("DropObject")) {            drObjStats = col.gameObject.GetComponent<DropObjectStats>();            if(parentObject == null) destroySpeed = 0;            else {                if((destroySpeed = defSpeed - (0.05f * parentObject.transform.childCount)) <= 0) {                    destroySpeed = 0;                }            }            if(!drObjStats.isTouchedToFloor) {                if(parentObject != null)Invoke("InvokedPlaySound", destroySpeed);                Destroy(col.gameObject, destroySpeed);            }            drObjStats.isTouchedToFloor = true;        }    }    void InvokedPlaySound() {        drSound.PlaySound();    }}